Examples
get_acs(
  geography = "tract",
  variables = c(male_u5 = "B01001_003", male_5to9 = "B01001_004"),
  year = 2023,
  state = "IL"
) |&gt;
  group_by(GEOID) |&gt;
  summarize(
    male_u10 = sum(estimate),
    male_u10_moe = moe_sum(moe, estimate)
  )

